mapper.select:
SELECT id, title, singer, song_no, brand, update_date FROM song WHERE brand = #{brand}
AND UPPER(singer) LIKE CONCAT('%',UPPER(#{singer}),'%')
AND UPPER(title) LIKE CONCAT('%',UPPER(#{title}),'%')
AND songNo LIKE CONCAT('%',#{songNo},'%')
AND id > #{offset} ORDER BY id ASC LIMIT #{limit}
